aws:cdk:cloudformation:props.statement:
permit(
    principal == ?principal,
    action in [ MyPolicyStore::Action::"ReadPhotos", MyPolicyStore::Action::"CreatePhoto" ], 
    resource == ?resource
)
when {
    true
};

static.statement:
permit(
    principal,
    action == MyPolicyStore::Action::"DeletePhoto", 
    resource
)
when {
    principal in MyPolicyStore::UserGroup::"admin"
};
